import './App.css';
import { useState, useEffect } from 'react'
import axios from 'axios'
import { LineChart, Line, CartesianGrid, XAxis, YAxis } from 'recharts';
import Navbar from './components/navbar/Navbar';
import DatePicker from './components/datePicker/DatePicker';

function App() {
  const url = 'http://data.fixer.io/api/'
  const access_key = '2fe0ad1d7c24d0016498ef8ec7b86137'
  const [symbols,setSymbols] = useState({});
  const data = [{name: 'Page A', uv: 400, pv: 2400, amt: 2400},{name: 'Page B', uv: 200, pv: 2400, amt: 2400},{name: 'Page C', uv: 300, pv: 2400, amt: 2400},{name: 'Page D', uv: 230, pv: 2400, amt: 2400},{name: 'Page E', uv: 404, pv: 2400, amt: 2400}];


  useEffect(()=>{
    getSymbols()
  },[])

  function getRates(){
    axios.get(`${url}latest?access_key=${access_key}`)
    .then(res => {
      console.log(res);
    })
  }

  function getSymbols(){
    axios.get(`${url}symbols?access_key=${access_key}`)
    .then(res => {
      if ( res.status === 200 ){
        setSymbols(res.data?.symbols);
      }
    })
  }

  return (
    <div className="App">
      <Navbar></Navbar>
      <header className="App-header">
      <DatePicker />
      <LineChart width={600} height={300} data={data}>
        <Line type="monotone" dataKey="uv" stroke="#8884d8" />
        <CartesianGrid stroke="#ccc" />
        <XAxis dataKey="name" />
        <YAxis />
      </LineChart>
        <button onClick={() => {getRates()}} >Obtener datos</button>
      </header>
      <section className='Sidebar'>
      <h6>Elige las divisas a mostrar</h6>
      <div className='check-wrapper'>
        {Object.keys(symbols).map((symbol)=> {
            return(
              <>
                <input type={'checkbox'} />
                <label>{symbol}</label>
              </>
            )
          })} 
      </div>
      </section>
    </div>
  );
}

export default App;
