import './App.css';
import { useState, useEffect } from 'react'
import axios from 'axios'
import Navbar from './components/navbar/Navbar';
import ConvertForm from './components/convertForm/ConvertForm';
import PlotForm from './components/plotForm/plotForm';
import { AlertTriangle, Archive, Battery, ArrowDownCircle } from 'react-feather';

function App() {
  const url = 'http://data.fixer.io/api/'
  const access_key = '2fe0ad1d7c24d0016498ef8ec7b86137'
  const [symbols,setSymbols] = useState({});
  let [tabState, setTabState] = useState(1);
  const services = [{name: 'Convert service', icon: <AlertTriangle size={50}/>, description: "Many desktop publishing packages and web page editors now use Lorem Ipsum as their default model text, and a search for 'lorem ipsum' will uncover many web sites still in their infancy. Various versions have evolved over the years, sometimes by accident, sometimes on purpose (injected humour and the like)."},{name: 'Convert service', icon: <Archive size={50} />, description: "It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that it has a more-or-less normal distribution of letters, as opposed to using 'Content here, content here', making it look like readable English."},{name: 'Convert service', icon: <Battery size={50} />, description: "It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that it has a more-or-less normal distribution of letters, as opposed to using 'Content here, content here', making it look like readable English."}]
  const data = [{date: '2020-05-03', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-04', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-05', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-06', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09}];


  useEffect(()=>{
    getSymbols()
  },[])

  function getRates(){
    axios.get(`${url}latest?access_key=${access_key}`)
    .then(res => {
      console.log(res);
    })
  }

  function getHistoricalRates(){
    axios.get(`${url}${'2020-05-03'}?access_key=${access_key}&symbols=${'MXN,CAD,USD,GBP'}`)
    .then(res => {
      console.log(res);
    })
  }

  function getSymbols(){
    axios.get(`${url}symbols?access_key=${access_key}`)
    .then(res => {
      if ( res.status === 200 ){
        setSymbols(res.data?.symbols);
      }
    })
  }

  return (
    <div className="App">
      <header>
      <Navbar></Navbar>
      </header>
      <section className='hero-section'>
        <h1>See your rates!</h1>
        <ArrowDownCircle size={50} color='white' />
      </section>
      <section>
        <article className='main-card'>
          <div className='tab-component'>
            <button className='active' onClick={() => setTabState(1)} >Convert</button>
            <button onClick={() => setTabState(0)}>Rates</button>
          </div>
          <div>
            {tabState === 1 ? <ConvertForm /> : <PlotForm data={data} />}
          </div>
        </article>
      </section>
      <section className="content-section">
        <table>
          <tr>
              <th>Company</th>
              <th>Contact</th>
              <th>Country</th>
          </tr>
          <tr>
            <td>Alfreds Futterkiste</td>
            <td>Maria Anders</td>
            <td>Germany</td>
          </tr>
          <tr>
            <td>Alfreds Futterkiste</td>
            <td>Maria Anders</td>
            <td>Germany</td>
          </tr>
          <tr>
            <td>Alfreds Futterkiste</td>
            <td>Maria Anders</td>
            <td>Germany</td>
          </tr>
          <tr>
            <td>Alfreds Futterkiste</td>
            <td>Maria Anders</td>
            <td>Germany</td>
          </tr>
          <tr>
            <td>Alfreds Futterkiste</td>
            <td>Maria Anders</td>
            <td>Germany</td>
          </tr>
          <tr>
            <td>Alfreds Futterkiste</td>
            <td>Maria Anders</td>
            <td>Germany</td>
          </tr>
          <tr>
            <td>Alfreds Futterkiste</td>
            <td>Maria Anders</td>
            <td>Germany</td>
          </tr>
          <tr>
            <td>Centro comercial Moctezuma</td>
            <td>Francisco Chang</td>
            <td>Mexico</td>
          </tr>
        </table>
      </section>
      <section className='services-section'>
        {services.map((service, index) => {
          return(
            <div className='service-card'>
              <span>{service.icon}</span>
              <h4>{service.name}</h4>
              <p>{service.description}</p>
            <button>Ver mas!</button>
        </div>
          )
        })}
      </section>
      <footer>
        <h6>Thanks for visit site</h6>
      </footer>
      {/* <section className='Sidebar'>
      <h6>Elige las divisas a mostrar</h6>
      <div className='check-wrapper'>
        {Object.keys(symbols).map((symbol, index)=> {
            return(
            <div className='check-item' key={index}>
                <input type={'checkbox'} />
                <label>{symbol}</label>  
            </div>)
          })} 
      </div>
      </section> */}
    </div>
  );
}

export default App;
