import React, { useState } from "react";
import axios from 'axios';
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Legend, Tooltip } from 'recharts';

export default  function PlotForm(){
    const url = 'http://data.fixer.io/api/'
    const access_key = '2fe0ad1d7c24d0016498ef8ec7b86137'
    let [dates,setDates] = useState([]);
    const data = [{date: '2020-05-03', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-04', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-05', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-06', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09}];


    function getHistoricalRates(date){
        axios.get(`${url}${date}?access_key=${access_key}&symbols=${'MXN,CAD,USD,GBP'}`)
        .then(res => {
          console.log(res);
        })
    }

    function handleDatePicker(e){
        const value = e.target.value
        console.log(value)
        setDates(...dates, value)
    }

    console.log('ates',dates)

    return(
        <div>
            <div>
                <h4>Indicate 4 dates:</h4>
                <form onSubmit={(e) => e.preventDefault()}>
                    <input type={"date"} onChange={handleDatePicker} />
                    <input type={"date"} />
                    <input type={"date"} />
                    <input type={"date"} />
                    <div>
                        <button>Plot</button>
                        <button>Clean</button>
                    </div>
                </form>
            </div>
            <div>
                <LineChart width={600} height={300} data={data}>
                <CartesianGrid stroke="#ccc" />
                <XAxis dataKey="date" angle={30} tick={{stroke: 'red', strokeWidth: 0.5}} label={"Fecha"} />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="MXN" stroke="#8884d8" />
                <Line type="monotoneX" dataKey="CAD" stroke="#82ca9d" />
                <Line type="monotoneY" dataKey="USD" stroke="#000000" />
                <Line type="linear" dataKey="GBP" stroke="#82ca9d" />
                </LineChart>
            </div>
        </div>

    )
}
