import './App.css';
import { useState, useEffect } from 'react'
import axios from 'axios'
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Legend, Tooltip } from 'recharts';
import Navbar from './components/navbar/Navbar';
import DatePicker from './components/datePicker/DatePicker';

function App() {
  const url = 'http://data.fixer.io/api/'
  const access_key = '2fe0ad1d7c24d0016498ef8ec7b86137'
  const [symbols,setSymbols] = useState({});
  const data = [{date: '2020-05-03', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-04', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-05', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-06', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09}];


  useEffect(()=>{
    getSymbols()
  },[])

  function getRates(){
    axios.get(`${url}latest?access_key=${access_key}`)
    .then(res => {
      console.log(res);
    })
  }

  function getHistoricalRates(){
    axios.get(`${url}${'2020-05-03'}?access_key=${access_key}&symbols=${'MXN,CAD,USD,GBP'}`)
    .then(res => {
      console.log(res);
    })
  }

  function getSymbols(){
    axios.get(`${url}symbols?access_key=${access_key}`)
    .then(res => {
      if ( res.status === 200 ){
        setSymbols(res.data?.symbols);
      }
    })
  }

  return (
    <div className="App">
      <header>
      <Navbar></Navbar>
      </header>
      <section className='hero-section'>
      </section>
      <section>
        <article className='main-card'>
        <DatePicker />
      <LineChart width={600} height={300} data={data}>
        <CartesianGrid stroke="#ccc" />
        <XAxis dataKey="date" angle={30} tick={{stroke: 'red', strokeWidth: 0.5}} label={"Fecha"} />
        <YAxis />
        <Tooltip />
        <Legend />
        <Line type="monotone" dataKey="MXN" stroke="#8884d8" />
        <Line type="monotoneX" dataKey="CAD" stroke="#82ca9d" />
        <Line type="monotoneY" dataKey="USD" stroke="#000000" />
        <Line type="linear" dataKey="GBP" stroke="#82ca9d" />
      </LineChart>
        </article>
      </section>
      <section className="content-section">
      <table>
  <tr>
    <th>Company</th>
    <th>Contact</th>
    <th>Country</th>
  </tr>
  <tr>
    <td>Alfreds Futterkiste</td>
    <td>Maria Anders</td>
    <td>Germany</td>
  </tr>
  <tr>
    <td>Centro comercial Moctezuma</td>
    <td>Francisco Chang</td>
    <td>Mexico</td>
  </tr>
</table>
      </section>
      <footer>
        <h6>Thanks for visit site</h6>
      </footer>
      {/* <section className='Sidebar'>
      <h6>Elige las divisas a mostrar</h6>
      <div className='check-wrapper'>
        {Object.keys(symbols).map((symbol, index)=> {
            return(
            <div className='check-item' key={index}>
                <input type={'checkbox'} />
                <label>{symbol}</label>  
            </div>)
          })} 
      </div>
      </section> */}
    </div>
  );
}

export default App;
