import './App.css';
import { useState, useEffect } from 'react'
import axios from 'axios'
import { LineChart, Line, CartesianGrid, XAxis, YAxis } from 'recharts';
import Navbar from './components/navbar/Navbar';
import DatePicker from './components/datePicker/DatePicker';

function App() {
  const url = 'http://data.fixer.io/api/'
  const access_key = '2fe0ad1d7c24d0016498ef8ec7b86137'
  const [symbols,setSymbols] = useState({});
  const data = [{date: '2020-05-03', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-04', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-05', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09},{date: '2020-05-06', CAD: 1.2, MXN: 27, GBP:.88, USD: 1.09}];


  useEffect(()=>{
    getSymbols()
  },[])

  function getRates(){
    axios.get(`${url}latest?access_key=${access_key}`)
    .then(res => {
      console.log(res);
    })
  }

  function getHistoricalRates(){
    axios.get(`${url}${'2020-05-03'}?access_key=${access_key}&symbols=${'MXN,CAD,USD,GBP'}`)
    .then(res => {
      console.log(res);
    })
  }

  function getSymbols(){
    axios.get(`${url}symbols?access_key=${access_key}`)
    .then(res => {
      if ( res.status === 200 ){
        setSymbols(res.data?.symbols);
      }
    })
  }

  return (
    <div className="App">
      <Navbar></Navbar>
      <header className="App-header">
      <DatePicker />
      <LineChart width={600} height={300} data={data.rates}>
        <Line type="monotone" dataKey="uv" stroke="#8884d8" />
        <CartesianGrid stroke="#ccc" />
        <XAxis dataKey="name" data={data.rates}/>
        <YAxis />
      </LineChart>
        <button onClick={() => {getRates()}} >Obtener datos</button>
        <button onClick={() => {getHistoricalRates()}} >convertir</button>
      </header>
      <section className='Sidebar'>
      <h6>Elige las divisas a mostrar</h6>
      <div className='check-wrapper'>
        {Object.keys(symbols).map((symbol)=> {
            return(
            <div className='check-item'>
                <input type={'checkbox'} />
                <label>{symbol}</label>  
            </div>)
          })} 
      </div>
      </section>
    </div>
  );
}

export default App;
